package homeassignements;

public class HAIsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int maxrange=10;
		for (int i = 1; i < maxrange+1; i++) {
			if(i%2==0) {
				System.out.println("Even Numbers between 1 to 10 : "+i);
			}
			
		}
	}

}
